mapper.update:
update bd_inquiry set 
		DEL_FL='Y' 
	where inq_id=#{inq_id}

update bd_inquiry set 
		subject=#{subject},
		inq_con=#{inq_con} 
	where inq_id=#{inq_id}

mapper.select:
select count(*)
from bd_inquiry
where user_email = #{user_email}
and inq_cate = #{inq_cate}
and DEL_FL='N'
and subject like '%'||#{subject}||'%'

select count(*)
from bd_inquiry
where user_email = #{user_email}
and DEL_FL='N'
and inq_cate = #{inq_cate}
and inq_con like '%'||#{subject}||'%'
