mapper.update:
update board
set use_fl = #{use_fl},name = #{name}
where board_id = #{board_id}

mapper.select:
select count(*)
from board_answer
where write_id = #{write_id}

mapper.update:
update board_write set 
    subject=#{subject},
    content=#{content}
where write_id= #{write_id}

update board_write set 
    del_yn='Y'
where write_id= #{write_id}

mapper.select:
select count(*) 
from board_write 
where board_id = #{board_id}
and user_email=#{user_email}
and del_yn='N'

mapper.update:
update board_write
set view_cnt = view_cnt + 1
where write_id= #{write_id}

mapper.select:
	select a.write_id,a.user_email,a.subject,a.writedate,b.comm_id,b.content from (select * from board_write)a inner join    
              (select * from board_answer)b on 
				a.write_id = b.write_id and a.user_email = #{user_email}

select count(*) 
from likes
where BOARD_ID=#{board_id} 
and LIKE_ID=#{like_id}
and USER_EMAIL = #{user_email}

mapper.update:
update board_write
set like_cnt = like_cnt + 1
where write_id= #{write_id}

update board_write
set like_cnt = like_cnt - 1
where write_id= #{write_id}

mapper.select:
select count(*)
from likes
where user_email = #{user_email}
and write_id = #{write_id}
