mapper.select:
SELECT	*
FROM	MEMO
WHERE	memo_date IN (TO_CHAR(SYSDATE-1, 'yyyyMMdd'), TO_CHAR(SYSDATE, 'yyyyMMdd'))
AND		PRJ_ID = #{prj_id}
AND		memo_email = #{memo_email}

SELECT	*
FROM	MEMO
WHERE	PRJ_ID = #{prj_id}
AND		memo_email = #{memo_email}

SELECT	*
FROM	MEMO
WHERE	PRJ_ID = #{prj_id}
AND		memo_email = #{memo_email}
AND		memo_date = #{memo_date}
