mapper.select:
SELECT * FROM (SELECT * FROM PROJECT)A INNER JOIN
			  (SELECT * FROM PROJECT_MEM)B ON
A.PRJ_ID = B.PRJ_ID AND B.USER_EMAIL = #{user_email}

mapper.update:
UPDATE  PROJECT 
SET 	PRJ_ST = #{prj_st}
WHERE   PRJ_ID = #{prj_id}

		UPDATE PROJECT A SET
					 
					 A.PRJ_NM = #{prj_nm}
					 
                     
					 , A.PRJ_EXP = #{prj_exp}
					 
                     , A.PRJ_AUTH = #{prj_auth}
                     
					 , A.PRJ_START_DT = #{prj_start_dt}
					 
					 
					 , A.PRJ_END_DT = #{prj_end_dt}
					 
					 
					 , A.PRJ_CMP_DT = #{prj_cmp_dt}
					 
                     , A.PRJ_ST = #{prj_st}
                     , A.PRJ_UPDATE = SYSDATE
                     , A.DEL_FL = 'N'
		WHERE A.PRJ_ID = #{prj_id}

mapper.select:
select * 
from project_mem
where prj_id = #{prj_id}
