mapper.insert:

	SELECT VOTE_SEQ.NEXTVAL FROM DUAL

INSERT INTO VOTE
	(
		 VOTE_ID
		,PRJ_ID
		,VOTE_EMAIL
		,VOTE_START_DATE
		,VOTE_END_DATE
		,VOTE_SUBJECT
		,VOTE_CON
		,VOTE_ANO
		,VOTE_ST
		,VOTE_DEL_FL
	)
	VALUES
	(
		 #{vote_id}
		,#{prj_id}
		,#{vote_email}
		,SYSDATE
		,#{vote_end_date,jdbcType=DATE}
		,#{vote_subject}
		,#{vote_con}
		,#{vote_ano,jdbcType=VARCHAR}
		,'P'
		,'N'
	)

mapper.update:
UPDATE	VOTE
SET		VOTE_DEL_FL = 'Y'
WHERE	VOTE_ID = #{vote_id}

mapper.insert:
MERGE INTO VOTE_ITEM
USING DUAL ON 
(
	VOTE_ITEM_ID IS NOT NULL AND
	VOTE_ITEM_ID = #{vote_item_id, jdbcType=INTEGER} AND
	VOTE_ID = #{vote_id}
)
WHEN MATCHED THEN
	UPDATE SET		
		VOTE_ITEM_CON = #{vote_item_con}
WHEN NOT MATCHED THEN
INSERT 
(
	VOTE_ITEM_ID
	,VOTE_ID
	,VOTE_ITEM_CON
) 
VALUES
(
	VOTE_IT_SEQ.NEXTVAL
	,#{vote_id}
	,#{vote_item_con}
)

MERGE INTO VOTE_PART
USING DUAL ON
	(
		VOTE_ID = #{vote_id} AND
		USER_EMAIL = #{user_email}
	)
WHEN MATCHED THEN
	UPDATE SET
		VOTE_ITEM_ID = #{vote_item_id}
WHEN NOT MATCHED THEN
INSERT 
(
	PRJ_ID
	,USER_EMAIL
	,VOTE_ID
	,VOTE_ITEM_ID
)
VALUES
(
	#{prj_id}
	,#{user_email}
	,#{vote_id}
	,#{vote_item_id}
)

mapper.select:
SELECT	USER_EMAIL, VOTE_ID, VOTE_ITEM_ID
FROM	VOTE_PART
WHERE	VOTE_ID = #{vote_id}

SELECT	*
FROM	VOTE_PART
WHERE	VOTE_ID = #{vote_id}
AND		USER_EMAIL = #{user_email}
