mapper.update:
UPDATE	WORK
SET		WRK_LST_ID = #{wrk_lst_id}
		,WRK_PR_ID = #{wrk_pr_id}
		,USER_EMAIL = #{user_email}
		,WRK_NM = #{wrk_nm}
		,WRK_GRADE = #{wrk_grade}
		,WRK_COLOR_CD = #{wrk_color_cd}
		,WRK_START_DT = #{wrk_start_dt}
		,WRK_END_DT = #{wrk_end_dt}
		,WRK_CMP_DT = #{wrk_cmp_dt, jdbcType=DATE}
		,WRK_CMP_FL = #{wrk_cmp_fl}
		,WRK_DEL_FL = #{wrk_del_fl}
WHERE	WRK_ID = #{wrk_id}

UPDATE WORK SET 
		
		 WRK_NM = #{wrk_nm}
		
		
                , WRK_GRADE = #{wrk_grade}
                
                
                , WRK_START_DT = #{wrk_start_dt}
                
                
                , WRK_END_DT = #{wrk_end_dt}
                
WHERE WRK_ID = #{wrk_id}

	UPDATE WORK SET 
					
						
							WRK_COLOR_CD = #{wrk_color_cd}
						
						
							WRK_COLOR_CD = NULL
						
					
 	WHERE WRK_ID = #{wrk_id}

update work_comment 
set del_fl='Y' 
where comm_id=#{comm_id} 
and prj_id =#{prj_id}

update work_comment 
set comm_content=#{comm_content}
where comm_id=#{comm_id} 
and prj_id =#{prj_id}

mapper.select:
select count(*) 
from work_comment 
where wrk_id = #{wrk_id}
and prj_id = #{prj_id}

SELECT A.*, B.USER_NM FROM WORK_MEM_FLW A, USERS B 
WHERE A.USER_EMAIL = B.USER_EMAIL 
AND A.WRK_ID = #{wrk_id}
AND A.USER_EMAIL = #{user_email}
